//Main
func init() {
	//Get First Hand and Set Default Colors
	hand = blackjack.GetHand()
	white = color.NRGBA{R: 255, G: 255, B: 255, A: 255}
	gray = color.NRGBA{R: 49, G: 48, B: 49, A: 255}
}

func main() {
	//Window Init
	app := app.New()
	window = app.NewWindow("Fyne Black Jack")
	window.Resize(fyne.NewSize(600, 400))
	window.SetFixedSize(true)
	
	//Loader (to stop screen flickering when creating new cards)
	popup = createPopup()
	popup.Hide()
	
	//Card Display
	cards := createCards(hand)
	handContainer = container.New(layout.NewHBoxLayout(), cards...)
	handCenterContainer := container.New(layout.NewCenterLayout(), handContainer, popup)
	
	//Hand Total Weight
	totalLabel = widget.NewLabel("Total: " + strconv.Itoa(hand.Total))
	totalDisplay := container.New(layout.NewCenterLayout(), totalLabel)
	
	//Control Buttons
	hitButton = widget.NewButton("Hit", func() {hit()})
	foldButton := widget.NewButton("Fold", func() {fold()})
	buttonContainer := container.New(layout.NewHBoxLayout(), hitButton, foldButton)
	buttonCenterContainer := container.New(layout.NewCenterLayout(), buttonContainer)
	
	//Content
	content := container.New(layout.NewVBoxLayout(), handCenterContainer, totalDisplay, buttonCenterContainer)
	
	//Window Set Content and Run
	window.SetContent(container.New(layout.NewCenterLayout(), content))
	window.CenterOnScreen()
	window.ShowAndRun()
}

//Util
func createCards(currentHand *blackjack.Hand) []fyne.CanvasObject {
	//Create visual display for cards
	var newCards []fyne.CanvasObject
	
	for _, element := range currentHand.Cards {		
		//Card Rect
		rect := canvas.NewRectangle(white)
		rect.SetMinSize(fyne.NewSize(100, 125))
		rect.FillColor = nil
		rect.StrokeColor = white
		rect.StrokeWidth = 2
		
		//Card Suit
		var suit *canvas.Image
		switch element.Suit {
			case "♠":
				suit = canvas.NewImageFromResource(resourceSpadePng)
			case "♥":
				suit = canvas.NewImageFromResource(resourceHeartPng)
			case "♣":
				suit = canvas.NewImageFromResource(resourceClubPng)
			default:
				suit = canvas.NewImageFromResource(resourceDiamondPng)
		}
		suit.FillMode = 2
		suit.ScaleMode = 0
		suit.Resize(fyne.NewSize(35, 60))
		suit.Move(fyne.NewPos(50, 80))
		
		//Card Symbol
		
		if (len(element.Symbol) < 2) {
			element.Symbol = " " + element.Symbol
		}
		symbol := canvas.NewText(element.Symbol, white)
		symbol.TextSize = 40
		symbol.Move(fyne.NewPos(88, 80))
		
		cardDataContainer := container.NewWithoutLayout(suit, symbol)
		cardContainer := container.New(layout.NewCenterLayout(), rect, cardDataContainer)
		
		newCards = append(newCards, cardContainer)
	}
	
	return newCards
}

func createPopup() *fyne.Container {
	//Create overlay for win/lose condition
	popupText = canvas.NewText("None", white)
	popupText.TextSize = 52
		
	rect := canvas.NewRectangle(gray)
	rect.SetMinSize(fyne.NewSize(580, 225))
	
	return container.New(layout.NewCenterLayout(), rect, popupText)
}

//Actions
func hit() error {
	//Add card to hand, then check to see if win/lose condition, if not rerender the cards, if so show popup with win/lose condition
	hand = blackjack.AddCard(hand)
	cards := createCards(hand)
	handContainer.Objects = cards
	totalLabel.SetText("Total: " + strconv.Itoa(hand.Total))
	
	result := blackjack.CheckHand(hand)
	
	if result != 0 {	
		hitButton.Disable()
				
		if result == 1 {
			popupText.Text = "Win"
		} else if result == 2 {
			popupText.Text = "Bust"
		}
		popupText.Refresh()
		
		handContainer.Hide()
		window.Resize(fyne.NewSize(600, 400))
		popup.Show()
	}
	
	return nil
}

func fold() error {
	//Discard current cards and get new ones, also hide popup and resize window in case it has gotten bigger
	hand = blackjack.GetHand()
	cards := createCards(hand)

	handContainer.Objects = cards
	totalLabel.SetText("Total: " + strconv.Itoa(hand.Total))
	
	hitButton.Enable()
	
	handContainer.Show()
	popup.Hide()
	window.Resize(fyne.NewSize(600, 400))
	
	return nil
}